"""### Variância Amostral"""

def GraphicAllVar(data, ns):
    fig, axes = plt.subplots(1, 3, figsize=(15, 5), sharey=True)

    for i, n in enumerate(ns, 1):
        W = np.zeros(10000)
        S = np.zeros(10000)
        for j in range(10000):
            Wi = np.random.choice(data, size=n, replace=True)
            W[j] = np.mean(Wi)
            S[j] = np.var(Wi, ddof=1)

        x_norm = np.linspace(np.min(S), np.max(S), 1000)
        p_norm = norm.pdf(x_norm, np.mean(S), np.std(S, ddof=1))

        # Parâmetros da distribuição gama
        scale = 2 * np.var(data) / (n - 1)
        a = (n - 1) / 2

        x_gamma = np.linspace(np.min(S), np.max(S), 1000)
        p_gamma = gamma.pdf(x_gamma, a, scale=scale)

        axes[i-1].hist(S, bins=30, density=True, alpha=0.6, label=f'n = {n}')
        axes[i-1].plot(x_norm, p_norm, label=f' PDF Normal Aproximada para n = {n}')
        axes[i-1].plot(x_gamma, p_gamma, label=f'Distribuição Gama para n = {n}, a = {a}, scale = {scale}', linestyle='--')

        axes[i-1].set_title(f'n = {n}')
        axes[i-1].set_xlabel('Valores da Variância Amostral (S)')

    axes[0].set_ylabel('Densidade de Probabilidade')
    axes[0].legend()
    axes[1].legend()
    plt.tight_layout()
    plt.show()

GraphicAllVar(dataQ['dataQ'], ns)

GraphicAllVar(dataX['dataX'], ns)

GraphicAllVar(dataY['dataY'], ns)

GraphicAllVar(dataT['dataT'], ns)

